import {withRouter} from "react-router";
import React, {Component, useState} from "react";

class status extends Component{

    constructor(props){
        super(props);
        this.state ={account:this.props.account, contract : this.props.contract, Status:false}
        this.getStatus().then(r => {console.log("Completed")});
    }
    getStatus = async () =>{
        let x = await this.props.contract.methods.ServiceProviders(this.state.account).call();
        let y= x.status
        this.setState({Status: y});
        console.log(x.status);
    }
    shoot=async(e)=> {
        e.preventDefault();
        await this.props.contract.methods.service_off().send({from:this.state.account});
        alert('submitted');


    }
    render(){
        return (
            <div>
                <br></br><br></br>
                <button onClick={this.shoot} style={{width:"100px"}}> Current Status :{this.state.Status} </button>
            </div>
        )}
}

export default withRouter(status);
