import React from "react";
import 'react-bootstrap';
import {useState} from 'react';

const DisplayServiceProviders = ({Account,Contract})=>{
    const onChange =async (e)=>{
        await Contract.methods.renderList(e.target.value).call();
    }
    return(
         <div>
            <label id={"service_type"}>Choose the service category :</label>
             <select id="service_type" className="form-control"  onChange={onChange} required>
             <option value = {0}>Select</option>
             <option value = {1}>Carpentry</option>
             <option value = {2}>Plumbing</option>
             <option value={3}>Painting</option>
             <option value ={4}>Electrical</option>
         </select>
         </div>
        )

}

export default DisplayServiceProviders;
