import {withRouter} from "react-router";
import React, {Component, useState} from "react";

class status extends Component{
    componentWillMount() {
        this.getStatus();
    }

    constructor(props){
        super(props);
        this.state ={account:this.props.account, contract : this.props.contract, Status:false}
    }
    shoot=async(e)=> {
        e.preventDefault();
        await this.state.contract.methods.service_off().send({from:this.state.account});
        alert('submitted');
        await this.getStatus();


    }
    getStatus = async() =>{
        let z= await this.props.Contract.methods.ServiceProviders(this.state.account).call();
        this.setState({Status: z.status});
        console.log(z.name);

    }
    render(){
        return (
            <div>
                <br></br><br></br>
                <button onClick={this.shoot} style={{width:"100px"}}> Current Status :{this.state.Status} </button>
            </div>
        )}
}

export default withRouter(status);
