import React, {useEffect} from "react";
import 'react-bootstrap';
class DisplayComponent extends React.Component{

    constructor(props) {
        super(props);
        this.state = {submitted: false}

    }
    async onSubmit (e){
        await this.props.contract.requestService(this.props.user.address)
            .send({from: this.props.Account, value: this.props.user.charges});
        console.log("pressed");
        this.setState({submitted:true})

    }
    render(){
        return(
          <div>
              <form onSubmit={this.onSubmit}>
              <table style={{"borderWidth":"5px", 'borderColor':"black", 'borderStyle':'solid'}}>
                  <thead>
                  <tr bgcolor={"yellow"}>
                      <th>Charges :{this.props.user.charges}</th>
                      <th colSpan="2">{this.props.user.name}<br></br> {this.props.user.city}</th>
                  </tr>
                  </thead>
                  <tbody>
                  <tr>
                      <td>{this.props.user.phone}</td>
                      <td>{this.props.user.email    }</td>
                      <td bgcolor={"red"}><button >Place Request for Service</button></td>
                  </tr>
                  </tbody>
              </table>
              </form>
          </div>
        ) }
}



export default DisplayComponent;
