import React, {Component, useState} from "react";
import '../css/RegisterScreen.css'
import validator from 'validator'
import {withRouter} from "react-router";

class RegisterServiceProvider extends Component{
    sp_type = 0;

    constructor(props) {
        super(props);
        this.state={emailError: '', phoneError :'', spAccount :this.props.Account, contract:this.props.contract};

    }

    validateEmail(e){
        let email = e.target.value;

        if (validator.isEmail(email)) {
            this.setState({emailError : 'Valid Email!'});
        }
        else {
            this.setState({emailError : 'Enter valid Email!'});
        }
    }

    validatePhoneNumber(e){
        const number = e.target.value;
        const isValidPhoneNumber = validator.isMobilePhone(number, 'en-IN')
        if(isValidPhoneNumber){
            this.setState({phoneError :'Valid Phone!'});
        }
        else {
            this.setState({phoneError : "Invalid Phone Number"});
        }
    }
    onChange = (e) => {
        this.sp_type= e.target.value;

    }

    onsubmit = async(e)=>{

        e.preventDefault();
        const name = e.target.elements.full_name.value;
        const location = e.target.elements.city.value;
        const email = e.target.elements.email.value;
        const phone = e.target.elements.phone.value;
        const charges = e.target.elements.charges.value;
        const service_type = this.sp_type;
        const address= e.target.elements.spAddress.value;
        await this.props.contract.methods.addServiceProvider(name,location, email,phone, charges,service_type ).send({from : address}).then(function(result){
            console.log("The funciton was succesfully terminated");
        });

    }
    render(){
        return(
            <div className="container" color= "lightblue">
                <form onSubmit={this.onsubmit}>
                    <h1>Sign Up for User</h1>
                    <p>Please fill in this form to create an account</p>
                    <input type ="text"  name = "userAddress" readOnly={true} value={this.state.spAccount = this.props.Account} disabled={true}/>
                    <input type="text" placeholder="Enter full name" name="full_name"   required/>
                    <input type="text" placeholder="Enter City" name="location" required/>
                    <input type="text" name="email" placeholder="Enter Email"
                           onChange={(e) => this.validateEmail(e)}/> <br/>
                    <span style={{
                        fontWeight: 'bold',
                        color: 'red',
                    }}>{this.state.emailError}</span>
                    <input type="text" name="phone" placeholder="Enter Phone"
                           onChange={(e) => this.validatePhoneNumber(e)}/> <br/>
                    <span style={{
                        fontWeight: 'bold',
                        color: 'red',
                    }}>{this.state.phoneError}</span>
                    <input type="number" placeholder="Enter the price in Ether" name="charges"/><br></br>
                    <select name="candidate" className="form-control" onChange={onchange} required>
                        <option defaultValue = {0}>Select</option>
                        <option value = {1}>Carpenter</option>
                        <option value = {2}>Plumber</option>
                        <option value ={3}>Electrician</option>
                        <option value = {4}>Painter</option>
                    </select><br></br>
                    <button className="btn btn-primary mt-2 btn-sm w-50">Sign Up{this.sp_type}</button>
                </form>
            </div>
        )}
}

export default withRouter(RegisterServiceProvider);
