import 'react-bootstrap'
import React,{useEffect, useState} from 'react';
import './App.css';
import Web3 from 'web3'
import ModelABI from './contracts/Model.json'

function App() {
  const [currentAccount, setCurrentAccount] = useState();
  const [contractABI, setContractABI] = useState("");

  useEffect(() => {
    loadWeb3();
    connectBlockChainData();
  },[]);

  const addUser = async () =>{
    await ModelABI
        .methods
        .addUser("Bhuwnesh Lohani", "Bangalore", "bhuwnesh.126@gmail.com","7579245939")
        .on("transactionHash", ()=>{
          console.log("succesfully added the user");
        });
  }
  const loadWeb3 = async () => {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  const connectBlockChainData = async() =>{
    const web3 = await new Web3(Web3.givenProvider || "http://localhost:8545")
    const accounts = await web3.eth.getAccounts()
    setCurrentAccount(accounts[0]);
    console.log(currentAccount);
    const networkId = await web3.eth.net.getId();
    const networkData = ModelABI.networks[networkId];
    if(networkData){

          await addUser();
    }
    else{
      window.alert("Please switch to Rinkeby Network!!")
    }
  }
  return (
    <div className="App">

          Learn React


    </div>
  );
}

export default App;
