import 'react-bootstrap'
import React, {useState} from 'react';
import './App.css';
import Web3 from 'web3'
import ModelABI from './contracts/Model.json';
import RegisterScreen from "./components/RegisterScreen";

class Welcome extends React.Component {
    componentWillMount() {
        this.loadWeb3().then(r=>{console.log("verified the browser support for ehtereum")})
        this.connectBlockChainData().then(r => {console.log("Data fecthed from blockchain")})
    }

  constructor(props) {
      super(props);
      this.currentAccount = null;
      this.contract = null;
      this.existingUser = null;
  }

  async getUserState(){
        let rvalue =0;
        await this.contract.methods.userExist(this.currentAccount).call()
          .then(function (result){
              rvalue = result;

          });
      return rvalue;
    }

  async addUser(name, location, email, phone){
    await this.contract
        .methods
        .addUser(name, location, email, phone)
        .send({from : this.currentAccount});

}


  async loadWeb3() {
    if (window.ethereum) {
      window.web3 = new Web3(window.ethereum)
      await window.ethereum.enable()
    }
    else if (window.web3) {
      window.web3 = new Web3(window.web3.currentProvider)
    }
    else {
      window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
    }
  }

  async RegisterUser(name, location, email, phone){
    await this.contract
        .methods
        .addUser(name, location, email, phone)
        .send({from : this.currentAccount}).then(function(result){
            console.log(result);
        });
  }



  async connectBlockChainData(){
    const web3 = await new Web3(Web3.givenProvider || "http://localhost:8545")
    const accounts = await web3.eth.getAccounts()
    this.currentAccount = accounts[0];
    console.log(accounts[0]);
    console.log(this.currentAccount);
    const networkId = await web3.eth.net.getId();
    const networkData = ModelABI.networks[networkId];
    if(networkData){
      const Model = new web3.eth.Contract(ModelABI.abi,networkData.address);
      this.contract = Model;
      const x = await Model.methods.getAllUsers().call()
      console.log(x);
        this.existingUser =await this.getUserState();
        console.log(this.existingUser);
    }
    else{
      window.alert("Please switch to Ganache Network!!")
    }
  }

  renderAuthButton(){
    if(this.existingUser == true)
        return <h1>hello</h1>
    else
        return <RegisterScreen Account ={this.currentAccount} Register={this.RegisterUser} />

  }
  render()
{
    return (

        <div className="App">
            {
                this.renderAuthButton()
            }
        </div>);
}


}

export default Welcome;
