import RegisterScreen from "./components/RegisterScreen";
function Welcome(){
    let [currentAccount, setCurrentAccount] = useState(0x0);
    let [contract, setContract] = useState(null);
    let [existingUser, setExistingUser] = useState(null);

    useEffect( () => {
            loadWeb3().then(r => {
                console.log("verified the browser support for ehtereum")
            })
            connectBlockChainData().then(r => {
                console.log("Data fecthed from blockchain")
            });
         },[]);

    const getUserState = async(contract)=>{
        let rvalue = false;
        while(contract !== null){
         await  contract.methods.userExist( currentAccount).call()
          .then(function (result){
              if(result == false)
                  RegisterScreenLoader();
          });}
        setExistingUser(rvalue);
        console.log(rvalue);
      return rvalue;
    }

  const addUser = async(name, location, email, phone)=>{

        console.log("...............................................................................................................................................................................................................ih655555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555555")
        await  contract
        .methods
        .addUser(name, location, email, phone)
        .send({from :  currentAccount});

}


  const loadWeb3 = async() => {
      if (window.ethereum) {
          window.web3 = new Web3(window.ethereum)
          await window.ethereum.enable()
      } else if (window.web3) {
          window.web3 = new Web3(window.web3.currentProvider)
      } else {
          window.alert('Non-Ethereum browser detected. You should consider trying MetaMask!')
      }
  }
  const RegisterUser = async(name, location, email, phone) =>{
        await  contract
        .methods
        .addUser(name, location, email, phone)
        .send({from :  currentAccount}).then(function(result){
            console.log(result);
        });
  }



  const connectBlockChainData = async()=>{
    const web3 = await new Web3(Web3.givenProvider || "http://localhost:8545")
    const accounts = await web3.eth.getAccounts()
     setCurrentAccount(accounts[0])
    console.log(accounts[0]);
    console.log( currentAccount);
    const networkId = await web3.eth.net.getId();
    const networkData = ModelABI.networks[networkId];
    if(networkData){
      const Model = await new web3.eth.Contract(ModelABI.abi,networkData.address);
      setContract(Model);
      const x = await Model.methods.getAllUsers().call();
      console.log(contract);
      console.log(x);
      let z = await Model.methods.Users(x[0]).call();
      console.log(z);
      let iop = await getUserState(contract)
        setExistingUser(iop)

    }
    else{
      window.alert("Please switch to Ganache Network!!")
    }
  }

  const RegisterScreenLoader = ()=>{
    if( existingUser === false)
        return <h1>hello</h1>
    else
        return <RegisterScreen Account ={ currentAccount}  Contract = { contract} />

  }

    return (

        <div className="App">
            {
                RegisterScreenLoader()
            }
        </div>);

}

export default Welcome;
