    },[]);

    const getUserState = async()=>{
        let count =0;
        while(count !== 100000000){
            count ++;
        }
        let rvalue = true;
        const promise = await  contract.methods.userExist( currentAccount).call()
          .then(function (result){
              rvalue = result;
          });
      return rvalue;
    }

  const addUser = async(name, location, email, phone)=>{

