import React, {Component, useState} from "react";
import '../css/RegisterScreen.css'
import validator from 'validator'
import {withRouter} from "react-router";
import {Link, useHistory} from 'react-router-dom';
import Main from '../Main';

class RegisterUser extends Component{

    emailError = '';
    phoneError = '';

    validateEmail(e){
        let email = e.target.value;

        if (validator.isEmail(email)) {
            this.emailError = 'Valid Email!';
        }
        else {
            this.emailError = 'Enter valid Email!';
        }
    }

    validatePhoneNumber(e){
        const number = e.target.value;
        const isValidPhoneNumber = validator.isMobilePhone(number, 'en-IN')
        if(isValidPhoneNumber){
            this.phoneError = 'Valid Phone!';
        }
        else {
            this.phoneError= "Invalid Phone Number";
        }
    }

    async onsubmit(e){
        e.preventDefault()
        const name = e.target.elements.fname.value;
        const location = e.target.elements.location.value;
        const email = e.target.elements.email.value;
        const phone = e.target.elements.phone.value;
        console.log(name + location +email+phone);
