import React, {useState} from "react";
import '../css/RegisterScreen.css'
import validator from 'validator'

const RegisterScreen = (Address) => {
    const [emailError, setEmailError] = useState('')
    const [phoneError, setPhoneError] = useState('')
    const validateEmail = (e) => {
        let email = e.target.value

        if (validator.isEmail(email)) {
            setEmailError('Valid Email!')
        }
         else {
            setEmailError('Enter valid Email!')
        }
    }

    const validatePhoneNumber = (e) => {
        const number = e.target.value;
        const isValidPhoneNumber = validator.isMobilePhone(number, 'en-IN')
        if(isValidPhoneNumber){
            setPhoneError('Valid Phone!')
        }
        else {
            setPhoneError("Invalid Phone Number")
        }
    }


        return(
      <div className="container" color= "lightblue">
          <form>

              <h1>Sign Up</h1>
              <p>Please fill in this form to create an account.</p>

              <select name="Enter type:" required>
                  <option value = "default">Select</option>
                  <option value ="user">User</option>
                  <option value = "service">Service Provider</option>
                  <option></option>
              </select>
              <input type="text" placeholder="Enter full name" id="fname"  required/>
              <input type="text" placeholder="Enter Location" id="location" required/>
              <input type="text" id="email"
                     onChange={(e) => validateEmail(e)}></input> <br />
              <span style={{
                  fontWeight: 'bold',
                  color: 'red',
              }}>{emailError}</span>
              <input type="text" id="phone"
                     onChange={(e) => validatePhoneNumber(e)}></input> <br />
              <span style={{
                  fontWeight: 'bold',
                  color: 'red',
              }}>{phoneError}</span>

          </form>
      </div>
  )
}

export default RegisterScreen
